import AddTodo from '../todo/AddTodo';
import Todo from '../todo/Todo';

function TodoList({ todos, isLoading, error, fetchTodos, setTodos }) {
  async function addTodoHandler(todo) {
    const response = await fetch(`${process.env.REACT_APP_API}/todos`, {
      method: 'POST',
      body: JSON.stringify(todo),
      header: {
        'Content-Type': 'application/json',
      },
    });
    const data = await response.json();
    console.log(data);
  }

  return (
    <div className="todo-list">
      {!todos.length && isLoading && <h3>Loading...</h3>}
      {!todos.length && !isLoading && error && (
        <div id="error">
          <h3>Error: Something went wrong</h3>
          <button className="btn btn--error" onClick={fetchTodos}>
            Refresh
          </button>
        </div>
      )}
      {!todos.length && !isLoading && !error && <h3>No tasks! ðŸ˜Š</h3>}
      {todos.map((todo, index) => {
        return (
          <Todo
            key={index}
            task={todo.task}
            id={todo._id}
            setTodos={setTodos}
            todos={todos}
          />
        );
      })}
      <AddTodo getTasks={fetchTodos} todos={todos} />
    </div>
  );
}

export default TodoList;
