function AddTodo(props) {
  return (
    <div className="card card--add">
      <form className="add-form">
        <label htmlFor="task" />
        <input
          type="text"
          className="input"
          id="task"
          value={form.task}
          onChange={(e) => updateForm({ task: e.target.value })}
          placeholder="Add a task"
          required
        ></input>
        <button className="btn">Add</button>
      </form>
    </div>
  );
}

export default AddTodo;
