import { useState, useEffect, useCallback } from 'react';
import AddTodo from './components/todo/AddTodo';
import ClearButton from './components/todo/ClearButton';
import Todo from './components/todo/Todo';
import Modal from './components/ui/Modal';
import Backdrop from './components/ui/Backdrop';

function App() {
  const [todos, setTodos] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(false);

  const fetchTaskshandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(`${process.env.REACT_APP_API}/todos`);
      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const data = await response.json();

      const todoTasks = data.results.map((task) => {
        return { task: task };
      });
      setTodos(todoTasks);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchTaskshandler();
  }, [fetchTaskshandler]);

  function deleteHandler() {
    setModalIsOpen(true);
  }
  function closeModalHandler() {
    setModalIsOpen(false);
  }

  return (
    <div className="main">
      <h1>To-Do List</h1>
      <div className="todo-list">
        {!todos.length && isLoading && <h3>Loading...</h3>}
        {!todos.length && !isLoading && error && (
          <div id="error">
            <h3>Error</h3>
            <button className="btn btn--error" onClick={getTasks}>
              Refresh
            </button>
          </div>
        )}
        {!todos.length && !isLoading && !error && <h3>No tasks! ðŸ˜Š</h3>}
        {todos.map((todo, index) => {
          return (
            <Todo
              key={index}
              task={todo.task}
              id={todo._id}
              setTodos={setTodos}
              todos={todos}
            />
          );
        })}
        <AddTodo getTasks={getTasks} todos={todos} />
      </div>
      <ClearButton onClick={deleteHandler} />
      {modalIsOpen && (
        <Modal
          onCancel={closeModalHandler}
          onConfirm={closeModalHandler}
          getTasks={getTasks}
        />
      )}
      {modalIsOpen && <Backdrop onClick={closeModalHandler} />}
    </div>
  );
}

export default App;
