import { useState, useEffect, useCallback } from 'react';
import AddTodo from './components/todo/AddTodo';
import ClearButton from './components/todo/ClearButton';
import Todo from './components/todo/Todo';
import Modal from './components/ui/Modal';
import Backdrop from './components/ui/Backdrop';
import TodoList from './components/todo/TodoList';

function App() {
  const [todos, setTodos] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(false);

  const fetchTodoshandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(`${process.env.REACT_APP_API}/todos`);
      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const data = await response.json();

      setTodos(data);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchTodoshandler();
  }, [fetchTodoshandler]);

  function deleteHandler() {
    setModalIsOpen(true);
  }
  function closeModalHandler() {
    setModalIsOpen(false);
  }

  async function addTodoHandler(todo) {
    const response = await fetch(`${process.env.REACT_APP_API}/todos`, {
      method: 'POST',
      body: JSON.stringify(todo),
      header: {
        'Content-Type': 'application/json',
      },
    });
    const data = await response.json();
    console.log(data);
  }

  return (
    <div className="main">
      <h1>To-Do List</h1>
      <TodoList todos={todos} />
      <ClearButton onClick={deleteHandler} />
      {modalIsOpen && (
        <Modal
          onCancel={closeModalHandler}
          onConfirm={closeModalHandler}
          getTasks={fetchTodoshandler}
        />
      )}
      {modalIsOpen && <Backdrop onClick={closeModalHandler} />}
    </div>
  );
}

export default App;
