import { useState, useEffect } from 'react';
import AddTodo from './components/todo/AddTodo';
import ClearButton from './components/todo/ClearButton';
import Todo from './components/todo/Todo';
import Modal from './components/ui/Modal';
import Backdrop from './components/ui/Backdrop';

function App() {
  const [todos, setTodos] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  async function getTasks() {
    const response = await fetch(`${process.env.REACT_APP_API}/todos`);

    if (!response.ok) {
      const message = `An error occured: ${response.statusText}`;
      window.alert(message);
      return;
    }

    const todos = await response.json();
    setTodos(todos);
  }

  useEffect(() => {
    getTasks();
    return;
  }, []);

  function deleteHandler() {
    setModalIsOpen(true);
  }
  function closeModalHandler() {
    setModalIsOpen(false);
  }

  const [loading, setLoading] = useState(false);
  const [collectionMarketInfo, setCollectionMarketInfo] = useState([null]);
  const [error, setError] = useState(null);

  const fetchData = async () => {
    // set authorization key and api url

    setLoading(true);

    const url = `https://api.blockvision.org/v1/2FzxL3Ege8cBfQqBEoHUtPBlBxP`;
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        id: 1,
        jsonrpc: '2.0',
        method: 'nft_collectionMarketInfo',
        params: {
          // tokenID: '1',
          contractAddress: '0xA7206d878c5c3871826DfdB42191c49B1D11F466',
          pageSize: 10,
          pageIndex: 0,
        },
      }),
    };
    // fetch data
    fetch(url, options)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setCollectionMarketInfo(data.result);
        // if (data && data.response == '') {
        //     data
        // }

        // setLoading(false)
      })
      .catch((err) => {
        console.log(err);
        setError('Error fetching data');
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchData();
  }, []);

  console.log(loading, collectionMarketInfo, error);

  return (
    <div className="main">
      <h1>To-Do List</h1>
      <div className="todo-list">
        {!todos.length && <h3>No tasks! ðŸ˜Š</h3>}
        {todos.map((todo, index) => {
          return (
            <Todo
              key={index}
              task={todo.task}
              id={todo._id}
              setTodos={setTodos}
              todos={todos}
            />
          );
        })}
        <AddTodo getTasks={getTasks} todos={todos} />
      </div>
      <ClearButton onClick={deleteHandler} />
      {modalIsOpen && (
        <Modal
          onCancel={closeModalHandler}
          onConfirm={closeModalHandler}
          getTasks={getTasks}
        />
      )}
      {modalIsOpen && <Backdrop onClick={closeModalHandler} />}
    </div>
  );
}

export default App;
